import React, { useState, useEffect } from "react";
import { Button } from "react-native";
import { ScrollView } from "react-native";
import { FlatList } from "react-native";
import { View, Text } from "react-native";
import { StyleSheet } from "react-native";
import newsAPI from "../../api/newsAPI";
import Article from "./Article";

const Explore = () => {
  const [articles, setArticles] = useState([]);

  useEffect(()=>{
      exploreAPI();
  })

  const exploreAPI = async () => {
    const response = await newsAPI.get('/everything', {
      params: {
        q: 'fitness AND exercise AND yoga',
        sortBy: 'relavancy',
      },
    });
    setArticles(response.data.articles);
  };

  return (
    <View style={styles.randm}>
      {/* <Button title="get articles" onPress={exploreAPI} /> */}
      {/* <Text style={styles.textStyle}> We found {articles.length} articles </Text> */}
      {/* <ScrollView> */}
          <FlatList data={articles} renderItem={(itemData) => (
              <Article key={itemData.index} title={itemData.item.title} urlToImage={itemData.item.urlToImage} />
          )} />
      {/* </ScrollView> */}
    </View>
  );
};

const styles = StyleSheet.create({
  textStyle: {
    fontSize: 30,
  },
  randm: {},
});

export default Explore;
